/*
Refactor the code so that it consists of three new components: Header, Content, and Total. 
All data still resides in the App component, which passes the necessary data to each component using props. 
Header takes care of rendering the name of the course, Content renders the parts and their number of exercises 
and Total renders the total number of exercises.

const App = () => {
  // const-definitions

  return (
    <div>
      <Header course={course} />
      <Content ... />
      <Total ... />
    </div>
  )
}
*/

const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
}

const Content = (props) => {
  return (
    <>
    <p>{props.part[0].name} {props.part[0].exercise}</p>
    <p>{props.part[1].name} {props.part[1].exercise}</p>
    <p>{props.part[2].name} {props.part[2].exercise}</p>
    </>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <h1>{course}</h1>
      <p>
        {part1} {exercises1}
      </p>
      <p>
        {part2} {exercises2}
      </p>
      <p>
        {part3} {exercises3}
      </p>
      <p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
    </div>
  )
}

export default App
